import { valueChanged, validateForm, VALIDATE_FORM, VALUE_CHANGED, NAME_FIELD, SURNAME_FIELD, EMAIL_FIELD, PHONE_FIELD } from './actions';

const initialState =
{
    formValues: {
        [NAME_FIELD]: '',
        [SURNAME_FIELD]: '',
        [EMAIL_FIELD]: '',
        [PHONE_FIELD]: ''
    }
}

export default function confirmationStepReducer(state = initialState, action) {
    switch (action.type) {
        case VALIDATE_FORM:
            return {
                ...state
            }
        case VALUE_CHANGED:
            return {
                ...state,
                formValues: { ...state.formValues, [action.field]: action.value }
            }
        default:
            return state
    }
}
