import { valueChanged, validateForm, VALIDATE_FORM, VALUE_CHANGED, NAME_FIELD, SURNAME_FIELD } from './actions';

const initialState =
{
    formValues: {
        [NAME_FIELD]: null,
        [SURNAME_FIELD]: null
    },
    validationResults: {
        [NAME_FIELD]: { validationPassed: true, validationMessage: null },
        [SURNAME_FIELD]: { validationPassed: true, validationMessage: null }
    }
}

export default function confirmationStepReducer(state = initialState, action) {
    switch (action.type) {
        case VALIDATE_FORM:
            return {
                ...state
            }
        case VALUE_CHANGED:
            debugger;
            return {
                ...state,
                [formValues[action.field]]: action.value
            }
        default:
            return state
    }
}
