import { RESERVATION_STEPS, NEXT_STEP, PREVIOUS_STEP, SET_STEP_VALID, SET_STEP_INVALID } from './actions';

const initialState =
{
    reservationStep: RESERVATION_STEPS[0],
    focusedInput: null,
    currentStepValid: false
}

export default function reservationApp(state = initialState, action)
{
    switch (action.type) {
        case NEXT_STEP:
            {
                const targetStep = RESERVATION_STEPS.find(x => x.value === state.reservationStep.value + 1);
                return {
                    ...state, ...{
                        reservationStep: targetStep || state.reservationStep
                    }
                }
            }
        case PREVIOUS_STEP: {
            const targetStep = RESERVATION_STEPS.find(x => x.value === action.index);
            return {
                ...state, ...{
                    reservationStep: targetStep || state.reservationStep
                }
            }
        }
        case SET_STEP_VALID:{
            return {
                ...state, ...{
                    currentStepValid: true
                }
            }
        }
        case SET_STEP_INVALID:{
            return {
                ...state, ...{
                    currentStepValid: false
                }
            }
        }
        default:
            return state
    }
}
