import { valueChanged, validateForm, VALIDATE_FORM, VALUE_CHANGED, NAME_FIELD, SURNAME_FIELD } from './actions';

const initialState =
{
    formValues: {
        [NAME_FIELD]: { value: null, validationPassed: true, validationMessage: null },
        [SURNAME_FIELD]: { value: null, validationPassed: true, validationMessage: null }
    }
}

export default function confirmationStepReducer(state = initialState, action) {
    switch (action.type) {
        case VALIDATE_FORM:
            return {
                ...state
            }
        case VALUE_CHANGED:
            return {
                ...state,
                formValues: { ...state.formValues, 
                    ...{ [action.field]: { value: action.value } } }
            }
        default:
            return state
    }
}
