import React from "react";
import { connect } from "react-redux";
import TextBox from '../../../../controls/textbox';
import SummaryComponent from '../../../summary/components';
import { valueChanged, formConfirmed, NAME_FIELD, SURNAME_FIELD, EMAIL_FIELD, PHONE_FIELD } from "../actions";
import Button from "../../../../controls/button";

class ConfirmationStepComponent extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        const { name, surname, email, phone, onFieldChange, onConfirm } = this.props;
        return (
            <div className='confirmation-container'>
                <div className="confirmation-form">
                    <form>
                        <TextBox value={name} validationResult={{}} onChange={(value) => onFieldChange(NAME_FIELD, value)} placeholder='Name' />
                        <TextBox value={surname} validationResult={{}} onChange={(value) => onFieldChange(SURNAME_FIELD, value)} placeholder='Surname' />
                        <TextBox value={email} validationResult={{}} onChange={(value) => onFieldChange(EMAIL_FIELD, value)} placeholder='Email' />
                        <TextBox value={phone} validationResult={{}} onChange={(value) => onFieldChange(PHONE_FIELD, value)} placeholder='Phone number' />
                        <div className='confirmation-row'>
                            <div className='summary-trigger-container'>
                                <SummaryComponent />
                            </div>
                            <Button onClick={()=> onConfirm()}>Confirm</Button>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}

const mapStateToProps = ({ confirmationStepReducer }) => {
    return {
        name: confirmationStepReducer.formValues[NAME_FIELD],
        surname: confirmationStepReducer.formValues[SURNAME_FIELD],
        email: confirmationStepReducer.formValues[EMAIL_FIELD],
        phone: confirmationStepReducer.formValues[PHONE_FIELD],
        nameValidationResult: { validationPassed: false, validationMessage: 'wrong' }
    };
};

const mapDispatchToProps = (dispatch, ownProps) => {
    return {
        onFieldChange: (fieldName, value) => dispatch(valueChanged(fieldName, value)),
        onConfirm: () => dispatch(formConfirmed())
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(ConfirmationStepComponent);
