import React from "react";
import Button from "../../controls/button";
import { connect } from "react-redux";
import { RESERVATION_STEPS, nextStep, previousStep } from "../actions";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import PropTypes from "prop-types";
import * as ReservationUtils from "../utils";
import { faShoppingCart } from "@fortawesome/free-solid-svg-icons";

function ReservationStepsNav({
  currentStep,
  currentStepValid,
  onNextStepClick,
  onPreviousStepClick
}) {
  return (
    <div className="reservation-steps-navigation">
      <div className="buttons-container">
        <div className="left-container">
          <FontAwesomeIcon icon={faShoppingCart} size="lg" />
        </div>
        <div className="right-container">
          {/* <Button
            text={"Previous"}
            disabled={ReservationUtils.isPreviousStepAvailable(
              currentStep.value
            )}
            onClick={onPreviousStepClick}
          ></Button> */}
          {/* <Button
            text={"Next"}
            disabled={
              ReservationUtils.isNextStepAvailable(currentStep.value) ||
              !currentStepValid
            }
            onClick={() =>
              currentStepValid && onNextStepClick(currentStep.value)
            }
          ></Button> */}
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = ({ reservationApp }) => ({
  currentStep: reservationApp.reservationStep,
  currentStepValid: reservationApp.currentStepValid
});

const mapDispatchToProps = dispatch => ({
  // onNextStepClick: () => dispatch(nextStep()),
  // onPreviousStepClick: () => dispatch(previousStep())
});

ReservationStepsNav.propTypes = {
  currentStep: PropTypes.shape({ value: PropTypes.number })
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ReservationStepsNav);
