export const RESERVATION_STEPS = [
  { value: 0, name: "Select date", route: "date", valid: false },
  { value: 1, name: "Select item", route: "vehicele", valid: false },
  { value: 2, name: "Select addons", route: "addons", valid: false },
  { value: 3, name: "Finalize", route: "finalize", valid: false }
];

// export const NEXT_STEP = "NEXT_STEP";
export const CHANGE_STEP = "CHANGE_STEP";
// export const PREVIOUS_STEP = "PREVIOUS_STEP";
export const SET_STEP_VALID = "SET_STEP_VALID";
export const SET_STEP_INVALID = "SET_STEP_INVALID";

// export const nextStep = () => ({ type: NEXT_STEP });
// export const previousStep = (index) => ({ type: PREVIOUS_STEP, index: index });
export const changeStep = (index) => ({ type: CHANGE_STEP, index: index });

// export const setCurrentStepValid = () => ({ type: SET_STEP_VALID });
// export const setCurrentStepInvalid = () => ({ type: SET_STEP_INVALID });

export const setStepValid = (index) => ({ type: SET_STEP_VALID, index });
export const setStepInvalid = (index) => ({ type: SET_STEP_INVALID, index })
