import React from "react";
import { connect } from "react-redux";
import TextBox from '../../../../controls/textbox';
import { valueChanged } from "../actions";

class ConfirmationStepComponent extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        const { name, surname, email, phone, onFieldChange } = this.props;
        return (<div className="confirmation-form-container">
            <form>
                <TextBox value={name} onChange={(value) => onFieldChange('name', value)} placeholder='Name' />
                <input type="text" placeholder="Name" />
                <input type="text" placeholder="Surname" />
                <input type="text" placeholder="Email address" />
                <input type="text" placeholder="Phone number" />
            </form>
        </div>
        );
    }
}

const mapStateToProps = () => {
    return {
        name: 'test',
        nameValidationResult: { validationPassed: false, validationMessage: 'wrong' }
    };
};

const mapDispatchToProps = (dispatch, ownProps) => {
    return {
        onFieldChange: (fieldName, value) => dispatch(valueChanged(fieldName, value))
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(ConfirmationStepComponent);
