import React from 'react';
import { connect } from 'react-redux';
import DateStepComponent from '../../date-step/components';
import ItemSelectStepComponent from '../../car-step/components';
import ReservationStepsState from './reservationStepsState';
import Button from '../../controls/button';
import ReservationStepsNav from './reservationStepsNav';

class ReservationContainer extends React.Component
{
    constructor()
    {
        super();
    }

    render()
    {
        return <div className='reservation-container'> 
            <ReservationStepsState />
            <div className="reservation-step-container">
                {this.currentStep()}
            </div>
            <ReservationStepsNav></ReservationStepsNav>
        </div>
    }

    currentStep()
    {
        switch (this.props.currentStep.value) {
            case 0: return <DateStepComponent />
            case 1: return <ItemSelectStepComponent />
            case 2: return <ExtrasStepComponent />
        }
    }
}

const mapStateToProps = ({ reservationApp }) =>
{
    return {
        currentStep: reservationApp.reservationStep
    }
}

export default connect(
    mapStateToProps
)(ReservationContainer)
