import {EMAIL_FIELD, NAME_FIELD, PHONE_FIELD, SURNAME_FIELD, VALUE_CHANGED, FORM_CONFIRMED} from './actions';

const requiredValidator = (value) => !!value;
const maxLengthValidator = (value, maxLength) => !!value && value.length < maxLength;
const minLengthValidator = (value, minLength) => !!value && value.length < minLength;

const initialState =
    {
        formValues: {
            [NAME_FIELD]: '',
            [SURNAME_FIELD]: '',
            [EMAIL_FIELD]: '',
            [PHONE_FIELD]: ''
        },
        formValidation: {
            [NAME_FIELD]: { isValid: false, validators:
                    [ requiredValidator,
                        value => maxLengthValidator(value, 100),
                        value => minLengthValidator(value, 2)
                    ] },
            [SURNAME_FIELD]: { isValid: false },
            [EMAIL_FIELD]: { isValid: false },
            [PHONE_FIELD]: { isValid: false }
        }
    }





export default function confirmationStepReducer(state = initialState, action) {
    switch (action.type) {
        case FORM_CONFIRMED:
            for (let formValidationKey in state.formValidation) {
                console.log(formValidationKey)
            }

            const validationResults = {};

            return {
                ...state,
                formValidation: validationResults
            }
        case VALUE_CHANGED:
            return {
                ...state,
                formValues: { ...state.formValues, [action.field]: action.value }
            }
        default:
            return state
    }
}
